import React, {Component} from 'react'
import {render} from 'react-dom'

let skiData = {
    goal: 100,
    total: 10
}

const Message = ({goal,total}) => {
    calcPercent = (decimal) => {
        return (decimal*100) + '%';
    }
    calcProgress = (total, goal) => {
        return this.calcPercent(total/goal);
    }
    return (
        <div>
            <span>Following is the data</span>
            <h4>Goal - {goal}</h4>
            <h4>Total - {total}</h4>
            <h4>Goal Progress - {this.calcProgress(total, goal)}</h4>
        </div>
    )
}

